CREATE TABLE `xm_config` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`key` varchar(50) DEFAULT NULL,	`value` text,	PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET = utf8mb4;
CREATE TABLE `xm_fanli` (	`id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,	`weid` varchar(64) NOT NULL,	`goodsid` bigint(20) UNSIGNED NOT NULL,	`goodstitle` varchar(1024) NOT NULL,	`money` float(5, 2) NOT NULL,	`bili` float(5, 2) NOT NULL,	`pid` varchar(64) NOT NULL,	`update_time` int(10) UNSIGNED NOT NULL,	`create_time` int(10) UNSIGNED NOT NULL,	`msgtext` varchar(1024) NOT NULL,	PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET = utf8mb4;
CREATE TABLE `xm_orders` (	`id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,	`orderid` bigint(20) UNSIGNED DEFAULT NULL,	`weid` varchar(64) DEFAULT NULL,	`goodsname` varchar(64) DEFAULT NULL,	`goodsid` bigint(20) UNSIGNED DEFAULT NULL,	`paymoney` float(5, 2) UNSIGNED DEFAULT NULL,	`pubmoney` float(5, 2) UNSIGNED DEFAULT NULL,	`pubbili` float(5, 2) UNSIGNED DEFAULT NULL,	`buymoney` float(5, 2) UNSIGNED DEFAULT NULL,	`buybili` float(5, 2) UNSIGNED DEFAULT NULL,	`income` float(5, 2) UNSIGNED DEFAULT NULL,	`status` int(10) UNSIGNED DEFAULT NULL,	`pid` varchar(64) DEFAULT NULL,	`update_time` int(10) UNSIGNED DEFAULT NULL,	`create_time` int(10) UNSIGNED DEFAULT NULL,	PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET = utf8mb4;
CREATE TABLE `xm_user` (	`id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,	`weid` varchar(64) DEFAULT NULL,	`wename` varchar(128) DEFAULT NULL,	`money` float(5, 2) UNSIGNED DEFAULT '0.00',	`check_time` int(10) UNSIGNED NOT NULL,	`update_time` int(10) UNSIGNED DEFAULT NULL,	`create_time` int(10) UNSIGNED DEFAULT NULL,	PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET = utf8mb4;
CREATE TABLE `xm_withdraw` (	`id` int(11) NOT NULL AUTO_INCREMENT,	`weid` varchar(64) NOT NULL,	`money` int(11) NOT NULL,	`status` tinyint(3) UNSIGNED NOT NULL DEFAULT '0',	`update_time` int(10) UNSIGNED NOT NULL,	`create_time` int(10) UNSIGNED NOT NULL,	PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET = utf8mb4;